templates.process_dynamic-es-task:
{{#if (any @root.data.notifications.subscriptions.[1])}}
{
  "name": "search_data",
  "taskModule": "elasticsearch",
  "renderer": "handlebars",
  "subs": {{{stringify @root.data.notifications}}},
  "config": {
    "connectionStringTemplate": "{{{@root.context.metaData.elasticsearchUrl}}}",
    "elasticsearchConfig": {
      "apiVersion": "5.6"
    },
    "queryTemplates": [
      {{#each (filter @root.data.notifications.subscriptions.[1] "campaign" "valueIn" (getKeys @root.context.metaData.campaignConfig))}}
        {{#withExtend this config=(getProperty @root.context.metaData.campaignConfig campaign)}}
          {
            "name": "{{{slugify (concat campaign ' ' email)}}}",
            "index": {{{stringify config.elasticsearchIndex}}},
            "type": {{{stringify config.elasticsearchType}}},
            "template": {{{stringify config.queryTemplate}}},
            "metaData": {{{stringify this}}}
          }{{#unless @last}},{{/unless}}
        {{/withExtend}}
      {{/each}}
    ]
  }
}
{{else}}
{
  "name": "search_data",
  "taskModule": "render",
  "renderer": "handlebars",
  "config": {
    "output": "data",
    "template": "{ \"status\": \"No more subscriptions to process\" }",
    "parseJson": true
  }
}
{{/if}}

templates.process_dynamic-mysql-task:
{{#ifAll (any @root.data.notifications.subscriptions.[1]) @root.data.search_data}}
  {{#json}}
    {{{json key="name" value="update_statuses"}}},
    {{{json key="taskModule" value="mysql"}}},
    {{{json key="renderer" value="handlebars"}}},
    {{#json key="config"}}
      {{{json key="connectionName" value="notifications"}}},
      {{#json key="queryTemplates" type="array"}}
        {{#each @root.data.notifications.subscriptions.[1]}}
          {{#withExtend this
              campaignConfig=(getProperty @root.context.metaData.campaignConfig campaign)
              recipientId=(slugify (concat campaign " " email))
              searchData=(getProperty @root.data.search_data (concat (slugify (concat campaign " " email)) ".hits.hits"))
            }}
            {{~#json type="string" ~}}
              CALL _log(
                NULL,
                {{{mysqlEscape (slugify (concat campaign " " email " " (random 1000000000 9999999999)))}}},
                {{{mysqlEscape campaign}}},
                {{{mysqlEscape email}}},
                {{#ifAny campaignConfig.sendIfEmpty
                    (ifAll
                      (default campaignConfig.importantType null)
                      (any (filter searchData "_type" "===" campaignConfig.importantType))
                    )
                    (ifAll
                      (if (default campaignConfig.importantType null) false else=true)
                      (any searchData)
                    )}}
                  {{{mysqlEscape 'sent-to-mailgun'}}}
                {{~else~}}
                  {{{mysqlEscape 'nothing-to-send'}}}
